mapper.insert:
INSERT INTO member
(
  member_id
, member_pwd
, member_email
, member_nick
, reg_member
, reg_datetime
, mod_member
, mod_datetime
)
VALUES
(
  #{id}
, #{password}
, #{email}
, #{nick}
, #{id}
, NOW()
, #{id}
, NOW()
)

mapper.update:
UPDATE member
   SET mod_member = #{id}
     , mod_datetime = NOW()

     , member_pwd = #{password}


     , member_email = #{email}


     , member_nick = #{nick}

 WHERE member_id = #{id}
